mapper.select:
SELECT
MEMBER_SEQ, EMAIL,PASSWORD,NAME,PHONE,INT_LOCAL,ENABLE FROM
skysearch.ss_member ORDER BY NAME ASC;

mapper.insert:

	SELECT MEMBER_SEQ
	FROM skysearch.ss_member
	WHERE MEMBER_SEQ =
	#{MEMBER_SEQ}


INSERT INTO skysearch.ss_member(
MEMBER_SEQ
,EMAIL
,PASSWORD
,NAME
,PHONE
,INT_LOCAL
,ENABLE
,REGISTER_SEQ
,REGISTRY_DATE
,MODIFIER_SEQ
,MODIFY_DATE
)
VALUES(
#{MEMBER_SEQ} 
, #{EMAIL} 
, #{PASSWORD} 
, #{NAME} 
, #{PHONE} 
, #{INT_LOCAL} 
, #{ENABLE} 
, #{REGISTER_SEQ} 
, now() 
, #{MODIFIER_SEQ} 
, now() )
ON DUPLICATE KEY UPDATE
EMAIL = #{EMAIL} 
, PASSWORD = #{PASSWORD}
, NAME = #{NAME} 
, PHONE = #{PHONE} 
, INT_LOCAL = #{INT_LOCAL}
, MODIFIER_SEQ = #{MODIFIER_SEQ} 
, MODIFY_DATE = now()
