mapper.insert:
INSERT INTO
skysearch.ss_member(
MEMBER_SEQ
,EMAIL
,PASSWORD
,NAME
,PHONE
,ENABLE
,REGISTER_SEQ
,REGISTRY_DATE
,MODIFIER_SEQ
,MODIFY_DATE
)
VALUES(
#{MEMBER_SEQ}
, #{EMAIL}
, #{PASSWORD}
, #{NAME}
, #{PHONE}
, #{ENABLE}
,
#{REGISTER_SEQ}
, now()
, #{MODIFIER_SEQ}
, now() )
ON DUPLICATE KEY
UPDATE
EMAIL = #{EMAIL}
, PASSWORD = #{PASSWORD}
, NAME = #{NAME}
, PHONE
= #{PHONE}
, MODIFIER_SEQ = #{MODIFIER_SEQ}
, MODIFY_DATE = now()
