mapper.insert:

INSERT INTO SKYSEARCH.SS_TRAVEL_INFO(
TRAVEL_SEQ	
,CITY_SEQ	
,LANDMARK_NAME
,LANDMARK_ADDR
,LANDMARK_EXP
,REGISTER_SEQ
,REGISTRY_DATE
,MODIFIER_SEQ
,MODIFY_DATE
)
VALUES(
#{TRAVEL_SEQ}
, #{CITY_SEQ} 
, #{LANDMARK_NAME}
, #{LANDMARK_ADDR}
, #{LANDMARK_EXP}  
, #{REGISTER_SEQ} 
, now() 
, #{MODIFIER_SEQ} 
, now() )
ON DUPLICATE KEY
UPDATE
LANDMARK_NAME = #{LANDMARK_NAME} 
, LANDMARK_EXP = #{LANDMARK_EXP}
, LANDMARK_ADDR = #{LANDMARK_ADDR}
, MODIFIER_SEQ = #{MODIFIER_SEQ} 
, MODIFY_DATE = now()


INSERT INTO 
SKYSEARCH.SS_CITY(
CITY_SEQ	
, COUNTRY_SEQ	
, CITY_NAME
, CITY_ADD
, CITY_LATITUDE
, CITY_LONGITUDE

)
VALUES(
#{CITY_SEQ}
, #{COUNTRY_SEQ} 
, #{CITY_NAME}
, #{CITY_ADD}
, #{CITY_LATITUDE}
, #{CITY_LONGITUDE}
)

ON DUPLICATE KEY
UPDATE
CITY_NAME = #{CITY_NAME} 
, CITY_ADD = #{CITY_ADD}
, CITY_LATITUDE = #{CITY_LATITUDE}
, CITY_LONGITUDE = #{CITY_LONGITUDE}
